/* Custom Tailwind Configuration */
tailwind.config = {
    theme: {
        extend: {
            colors: {
                'cream-100': '#fdfaf6',
                'green-700': '#3d5c52',
                'green-800': '#304740',
                'orange-500': '#f56565',
                'orange-600': '#e53e3e',
            },
            fontFamily: {
                'playfair': ['"Playfair Display"', 'serif'],
                'roboto': ['"Roboto"', 'sans-serif'],
            },
            keyframes: {
                'fade-in-up': {
                    '0%': {
                        opacity: '0',
                        transform: 'translateY(20px)'
                    },
                    '100%': {
                        opacity: '1',
                        transform: 'translateY(0)'
                    },
                }
            },
            animation: {
                'fade-in-up': 'fade-in-up 0.8s ease-out forwards',
            }
        }
    }
}

/* Global Styles */
body {
    @apply bg-cream-100;
}

/* Navbar Styles */
.nav-link {
    @apply text-gray-600 font-medium hover:text-orange-500 transition-colors duration-300 relative;
}

.nav-link::after {
    content: '';
    @apply absolute bottom-0 left-0 w-full h-0.5 bg-orange-500 scale-x-0 origin-left transition-transform duration-300;
    transform: translateY(4px);
}

.nav-link:hover::after,
.nav-link.active::after {
    @apply scale-x-100;
}

.nav-link.active {
    @apply text-orange-500;
}


/* Category Card Styles */
.category-card {
    @apply bg-white p-6 rounded-lg shadow-md hover:shadow-xl hover:-translate-y-2 transition-all duration-300 flex flex-col items-center justify-center;
}

.category-card .icon-wrapper {
    @apply w-20 h-20 bg-orange-500/10 text-orange-500 rounded-full flex items-center justify-center text-3xl transition-all duration-300;
}

.category-card:hover .icon-wrapper {
    @apply bg-orange-500 text-white scale-110;
}

/* Recipe Card Styles */
.recipe-card {
    @apply bg-white rounded-lg shadow-md overflow-hidden transition-all duration-300 hover:shadow-xl hover:-translate-y-1;
}

.recipe-card-image {
    @apply w-full h-48 object-cover;
}

.recipe-card-content {
    @apply p-5;
}

.recipe-card-title {
    @apply font-playfair text-xl font-bold text-green-800 mb-2 truncate;
}

.recipe-card-description {
    @apply text-gray-600 text-sm mb-4 h-10 overflow-hidden;
}

.recipe-card-tag {
    @apply text-xs font-semibold bg-green-700/10 text-green-800 px-2 py-1 rounded-full;
}

/* Footer Link */
.footer-link {
    @apply text-gray-400 hover:text-orange-500 hover:pl-2 transition-all duration-300;
}

/* Recipe Detail Page */
.prose ol > li::marker {
    @apply font-bold text-green-800;
}

.prose ol > li {
    @apply mb-4;
}

.prose p {
    @apply leading-relaxed;
}

.social-share-btn {
    @apply w-12 h-12 rounded-full flex items-center justify-center text-white text-xl hover:opacity-80 transition-opacity;
}

/* Guide Card Styles */
.guide-card {
    @apply bg-white p-8 rounded-lg shadow-lg text-center transition-all duration-300 hover:shadow-2xl hover:scale-105;
}

/* Gluten-free icon rotation fix */
.fa-bread-slice {
    transform: rotate(-30deg);
}

document.addEventListener('DOMContentLoaded', function() {
    // --- General Site-wide Scripts ---

    // Mobile Menu Toggle
    const mobileMenuButton = document.getElementById('mobile-menu-button');
    const mobileMenu = document.getElementById('mobile-menu');
    if (mobileMenuButton) {
        mobileMenuButton.addEventListener('click', () => {
            mobileMenu.classList.toggle('hidden');
        });
    }

    // Sticky Navbar
    const navbar = document.getElementById('navbar');
    if (navbar) {
        window.addEventListener('scroll', () => {
            if (window.scrollY > 50) {
                navbar.classList.add('py-2');
                navbar.classList.remove('py-4');
            } else {
                navbar.classList.add('py-4');
                navbar.classList.remove('py-2');
            }
        });
    }

    // Newsletter Form
    const newsletterForm = document.getElementById('newsletter-form');
    if (newsletterForm) {
        const emailInput = document.getElementById('newsletter-email');
        const feedbackEl = document.getElementById('newsletter-feedback');
        newsletterForm.addEventListener('submit', (e) => {
            e.preventDefault();
            feedbackEl.textContent = `Thank you! ${emailInput.value} has been subscribed.`;
            feedbackEl.classList.add('text-white');
            emailInput.value = '';
            setTimeout(() => {
                feedbackEl.textContent = '';
            }, 5000);
        });
    }

    // --- Homepage Specific Scripts ---
    if (document.getElementById('featured-recipes-grid')) {
        const featuredGrid = document.getElementById('featured-recipes-grid');
        // Get 3 random recipes to feature
        const featuredRecipes = allRecipes.sort(() => 0.5 - Math.random()).slice(0, 3);
        featuredRecipes.forEach(recipe => {
            featuredGrid.innerHTML += createRecipeCard(recipe);
        });
    }
});

// --- Reusable Functions ---

// Function to create a recipe card HTML string
function createRecipeCard(recipe) {
    // Get average rating
    const ratings = JSON.parse(localStorage.getItem('recipeRatings')) || {};
    const recipeRatings = ratings[recipe.id] || [];
    const avgRating = recipeRatings.length > 0 
        ? (recipeRatings.reduce((a, b) => a + b, 0) / recipeRatings.length).toFixed(1)
        : recipe.rating.toFixed(1);

    const ratingStars = generateStars(avgRating);

    return `
        <div class="recipe-card animate-fade-in-up">
            <a href="/recipe-detail?id=${recipe.id}" class="block">
                <img src="${recipe.image}" alt="${recipe.title}" class="recipe-card-image">
                <div class="recipe-card-content">
                    <div class="flex justify-between items-start">
                         <h3 class="recipe-card-title">${recipe.title}</h3>
                         <span class="recipe-card-tag">${recipe.cuisine}</span>
                    </div>
                    <p class="recipe-card-description">${recipe.description}</p>
                    <div class="flex justify-between items-center">
                        <div class="flex items-center text-sm text-gray-500">
                            <i class="far fa-clock mr-2 text-orange-500"></i>
                            ${recipe.prepTime} + ${recipe.cookTime}
                        </div>
                        <div class="flex items-center">
                            <span class="text-yellow-500 mr-1">${ratingStars}</span>
                            <span class="text-sm text-gray-600 font-bold">${avgRating}</span>
                        </div>
                    </div>
                </div>
            </a>
        </div>
    `;
}

// Function to generate star icons based on rating
function generateStars(rating) {
    let stars = '';
    for (let i = 1; i <= 5; i++) {
        if (i <= rating) {
            stars += '<i class="fas fa-star"></i>';
        } else if (i - rating < 1 && i - rating > 0) {
            stars += '<i class="fas fa-star-half-alt"></i>';
        } else {
            stars += '<i class="far fa-star"></i>';
        }
    }
    return stars;
}

document.addEventListener('DOMContentLoaded', () => {
    const recipesGrid = document.getElementById('recipes-grid');
    const searchInput = document.getElementById('search-input');
    const cuisineFilter = document.getElementById('cuisine-filter');
    const dietFilter = document.getElementById('diet-filter');
    const noResultsDiv = document.getElementById('no-results');
    const resultsCount = document.getElementById('results-count');

    let currentRecipes = [...allRecipes];

    // Populate filter dropdowns
    function populateFilters() {
        const cuisines = [...new Set(allRecipes.map(r => r.cuisine))];
        cuisines.sort().forEach(c => {
            cuisineFilter.innerHTML += `<option value="${c}">${c}</option>`;
        });

        const diets = [...new Set(allRecipes.flatMap(r => r.dietaryTags))];
        const uniqueDiets = [...new Set(diets)];
        uniqueDiets.sort().forEach(d => {
            dietFilter.innerHTML += `<option value="${d}">${d}</option>`;
        });
    }

    // Render recipes to the grid
    function renderRecipes(recipes) {
        recipesGrid.innerHTML = '';
        if (recipes.length === 0) {
            noResultsDiv.classList.remove('hidden');
            recipesGrid.classList.add('hidden');
            resultsCount.textContent = '0 recipes found.';
        } else {
            noResultsDiv.classList.add('hidden');
            recipesGrid.classList.remove('hidden');
            resultsCount.textContent = `${recipes.length} recipe${recipes.length > 1 ? 's' : ''} found.`;
            recipes.forEach(recipe => {
                recipesGrid.innerHTML += createRecipeCard(recipe);
            });
        }
    }

    // Filter and search logic
    function filterAndSearch() {
        const searchTerm = searchInput.value.toLowerCase();
        const selectedCuisine = cuisineFilter.value;
        const selectedDiet = dietFilter.value;

        let filteredRecipes = [...allRecipes];

        // Filter by cuisine
        if (selectedCuisine !== 'all') {
            filteredRecipes = filteredRecipes.filter(r => r.cuisine === selectedCuisine);
        }

        // Filter by diet
        if (selectedDiet !== 'all') {
            filteredRecipes = filteredRecipes.filter(r => r.dietaryTags.includes(selectedDiet));
        }

        // Filter by search term
        if (searchTerm) {
            filteredRecipes = filteredRecipes.filter(r => 
                r.title.toLowerCase().includes(searchTerm) ||
                r.description.toLowerCase().includes(searchTerm) ||
                r.ingredients.some(ing => ing.toLowerCase().includes(searchTerm))
            );
        }

        currentRecipes = filteredRecipes;
        renderRecipes(currentRecipes);
    }

    // Check for URL parameters and apply filters
    function applyUrlParams() {
        const params = new URLSearchParams(window.location.search);
        const cuisine = params.get('cuisine');
        const diet = params.get('diet');

        if (cuisine) {
            cuisineFilter.value = cuisine;
        }
        if (diet) {
            dietFilter.value = diet;
        }

        filterAndSearch();
    }

    // Event Listeners
    searchInput.addEventListener('input', filterAndSearch);
    cuisineFilter.addEventListener('change', filterAndSearch);
    dietFilter.addEventListener('change', filterAndSearch);

    // Initial setup
    populateFilters();
    applyUrlParams();
});

document.addEventListener('DOMContentLoaded', () => {
    const loadingSpinner = document.getElementById('loading-spinner');
    const contentWrapper = document.getElementById('recipe-content-wrapper');

    // Get recipe ID from URL
    const params = new URLSearchParams(window.location.search);
    const recipeId = parseInt(params.get('id'));
    const recipe = allRecipes.find(r => r.id === recipeId);

    if (recipe) {
        // Hide spinner and show content
        loadingSpinner.style.display = 'none';
        contentWrapper.classList.remove('opacity-0');

        // Populate page with recipe data
        document.title = `${recipe.title} - Flavor Adventures`;
        document.getElementById('breadcrumb-recipe-title').textContent = recipe.title;
        document.getElementById('recipe-title').textContent = recipe.title;
        document.getElementById('recipe-description').textContent = recipe.description;
        document.getElementById('recipe-image').src = recipe.image;
        document.getElementById('recipe-image').alt = recipe.title;
        document.getElementById('prep-time').textContent = recipe.prepTime;
        document.getElementById('cook-time').textContent = recipe.cookTime;
        document.getElementById('servings').textContent = recipe.servings;

        // Populate ingredients
        const ingredientsList = document.getElementById('ingredients-list');
        recipe.ingredients.forEach(ing => {
            ingredientsList.innerHTML += `
                <li class="flex items-center">
                    <input type="checkbox" class="h-4 w-4 rounded border-gray-300 text-orange-600 focus:ring-orange-500 mr-3">
                    <span>${ing}</span>
                </li>`;
        });

        // Populate instructions
        const instructionsList = document.getElementById('instructions-list');
        recipe.instructions.forEach(step => {
            instructionsList.innerHTML += `<p>${step}</p>`;
        });

        // Rating System
        const stars = document.querySelectorAll('#rating-stars i');
        const ratingFeedback = document.getElementById('rating-feedback');
        let currentRating = 0;

        function loadRating() {
            const ratings = JSON.parse(localStorage.getItem('recipeRatings')) || {};
            if (ratings[recipe.id] && ratings[recipe.id].userRating) {
                currentRating = ratings[recipe.id].userRating;
                updateStars(currentRating);
                ratingFeedback.textContent = 'You rated this recipe!';
            }
        }

        function updateStars(rating) {
            stars.forEach(star => {
                star.classList.remove('fas', 'text-yellow-400');
                star.classList.add('fa-solid', 'text-gray-300');
                if (star.dataset.value <= rating) {
                    star.classList.add('fas', 'text-yellow-400');
                    star.classList.remove('text-gray-300');
                }
            });
        }

        stars.forEach(star => {
            star.addEventListener('mouseover', () => updateStars(star.dataset.value));
            star.addEventListener('mouseout', () => updateStars(currentRating));
            star.addEventListener('click', () => {
                currentRating = star.dataset.value;
                saveRating(currentRating);
                ratingFeedback.textContent = 'Thanks for your rating!';
                setTimeout(() => { ratingFeedback.textContent = 'You rated this recipe!' }, 3000);
            });
        });

        function saveRating(rating) {
            const ratings = JSON.parse(localStorage.getItem('recipeRatings')) || {};
            if (!ratings[recipe.id]) {
                ratings[recipe.id] = { all: [], userRating: null };
            }
            ratings[recipe.id].all.push(parseInt(rating)); // Store all ratings
            ratings[recipe.id].userRating = parseInt(rating); // Store this user's specific rating
            localStorage.setItem('recipeRatings', JSON.stringify(ratings));
        }

        // Social Sharing
        const shareLinks = document.querySelector('#social-share');
        const pageUrl = window.location.href;
        const pageTitle = encodeURIComponent(recipe.title);
        shareLinks.querySelector('.bg-blue-600').href = `https://www.facebook.com/sharer/sharer.php?u=${pageUrl}`;
        shareLinks.querySelector('.bg-blue-400').href = `https://twitter.com/intent/tweet?url=${pageUrl}&text=${pageTitle}`;
        shareLinks.querySelector('.bg-red-600').href = `https://pinterest.com/pin/create/button/?url=${pageUrl}&media=${encodeURIComponent(recipe.image)}&description=${pageTitle}`;

        // Initial load
        loadRating();
    } else {
        // Handle recipe not found
        loadingSpinner.style.display = 'none';
        contentWrapper.innerHTML = `
            <div class="text-center py-16">
                <i class="fas fa-exclamation-circle fa-3x text-red-500 mb-4"></i>
                <h1 class="text-4xl font-playfair text-gray-700">Recipe Not Found</h1>
                <p class="text-gray-500 mt-2">We couldn't find the recipe you're looking for.</p>
                <a href="/recipes" class="mt-6 inline-block bg-orange-500 text-white px-6 py-3 rounded-full hover:bg-orange-600 transition-colors">Back to All Recipes</a>
            </div>`;
        contentWrapper.classList.remove('opacity-0');
    }
});
